targets.expr:
(
  sum by (job_id, operator_id, operator_name, subtask_index) (
    increase(flink_taskmanager_job_task_operator_rocksdb_block_cache_miss[15m])
  )
)
/
sum by (job_id, operator_id, operator_name, subtask_index) (
  10*increase(flink_taskmanager_job_task_operator_state_name_mapStateGetLatency_count[15m])
)

(
  sum by (job_id, operator_id, operator_name, subtask_index) (
    increase(flink_taskmanager_job_task_operator_rocksdb_block_cache_hit[15m])
  )
)
/
sum by (job_id, operator_id, operator_name, subtask_index) (
  10*increase(flink_taskmanager_job_task_operator_state_name_mapStateGetLatency_count[15m])
)

(
  sum by (job_id, operator_id, operator_name, subtask_index) (
    increase(flink_taskmanager_job_task_operator_rocksdb_block_cache_hit[15m])
    + increase(flink_taskmanager_job_task_operator_rocksdb_block_cache_miss[15m])
  )
)
/
sum by (job_id, operator_id, operator_name, subtask_index) (
  10*increase(flink_taskmanager_job_task_operator_state_name_mapStateGetLatency_count[15m])
)

avg_over_time(
  sum by (job_name, operator_name) (
    flink_taskmanager_job_task_operator_numRecordsOutPerSecond{
      cluster="nexmark-flink",
      job_name=~"$job",
      operator_name=~".*Source.*"
    }
  )[20m:]
)

(
  sum without(instance, taskmanager_id) (
    increase(
      flink_taskmanager_job_task_operator_rocksdb_block_cache_data_hit{
        cluster="nexmark-flink",
        job_name=~"$job"
      }[20m]
    )
  )
)
/
(
  sum without(instance, taskmanager_id) (
    increase(
      flink_taskmanager_job_task_operator_rocksdb_block_cache_data_hit{
        cluster="nexmark-flink",
        job_name=~"$job"
      }[20m]
    )
  )
  +
  sum without(instance, taskmanager_id) (
    increase(
      flink_taskmanager_job_task_operator_rocksdb_block_cache_data_miss{
        cluster="nexmark-flink",
        job_name=~"$job"
      }[20m]
    )
  )
)
* 100

sum by (job_name, operator_name) (
  max by (job_name, operator_name, subtask_index) (
    flink_taskmanager_job_task_operator_column_family_rocksdb_block_cache_usage{cluster="nexmark-flink", job_name=~"$job"}
  )
)

sum by (job_name, operator_name) (
  max by (job_name, operator_name, subtask_index) (
    flink_taskmanager_job_task_operator_column_family_rocksdb_block_cache_capacity{cluster="nexmark-flink", job_name=~"$job"}
  )
)

sum by (job_name, operator_name) (
  max by (job_name, operator_name, subtask_index) (
    flink_taskmanager_job_task_operator_column_family_rocksdb_block_cache_pinned_usage{cluster="nexmark-flink", job_name=~"$job"}
  )
)

(
  sum without(instance, taskmanager_id) (
    increase(
      flink_taskmanager_job_task_operator_rocksdb_block_cache_hit{
        cluster="nexmark-flink",
        job_name=~"$job"
      }[20m]
    )
  )
)
/
(
  sum without(instance, taskmanager_id) (
    increase(
      flink_taskmanager_job_task_operator_rocksdb_block_cache_hit{
        cluster="nexmark-flink",
        job_name=~"$job"
      }[20m]
    )
  )
  +
  sum without(instance, taskmanager_id) (
    increase(
      flink_taskmanager_job_task_operator_rocksdb_block_cache_miss{
        cluster="nexmark-flink",
        job_name=~"$job"
      }[20m]
    )
  )
)
* 100

quantile_over_time(0.5,
  sum by (job_name, operator_name) (
    flink_taskmanager_job_task_operator_numRecordsOutPerSecond{
      cluster="nexmark-flink", 
      job_name=~"$job", 
      operator_name=~".*Source.*"
    }
  )[20m:]
)

sum by (job_name, operator_name) (
  max by (job_name, operator_name, subtask_index) (
    flink_taskmanager_job_task_operator_column_family_rocksdb_block_cache_usage{cluster="nexmark-flink", job_name=~"$job"}
  )
)

sum by (job_name, operator_name) (
  max by (job_name, operator_name, subtask_index) (
    flink_taskmanager_job_task_operator_column_family_rocksdb_block_cache_capacity{cluster="nexmark-flink", job_name=~"$job"}
  )
)

sum by (job_name, operator_name) (
  max by (job_name, operator_name, subtask_index) (
    flink_taskmanager_job_task_operator_column_family_rocksdb_block_cache_pinned_usage{cluster="nexmark-flink", job_name=~"$job"}
  )
)
